<?php get_header(); ?><div id="main"><div id="content"><?php if ( have_posts() ) the_post(); ?><h1><?php if ( is_day() ) : printf( __( '<span>%s</span> Archives','boiler'), get_the_date() ); elseif ( is_month() ) : printf( __( '<span>%s</span> Archives','boiler'), get_the_date( _x( 'F Y', 'monthly archives date format','boiler') ) ); elseif ( is_year() ) : printf( __( '<span>%s</span> Archives','boiler'), get_the_date( _x( 'Y', 'yearly archives date format') ) ); else :  single_cat_title(); ?> <?php _e('Archives','boiler'); ?><?php endif; ?></h1><h2><?php echo $wp_query->found_posts; ?> <?php _e('results:','boiler'); ?></h2><?php rewind_posts(); get_template_part( 'loop' ); ?></div><ul id="blogSidebar"><?php if ( !function_exists('dynamic_sidebar') || !dynamic_sidebar("sidebar") ) : endif; ?></ul></div><?php get_footer(); ?>