<?php $date_format = get_option( 'date_format' );$time_format = get_option( 'time_format' ); ?><?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?><div id="post-<?php the_ID(); ?>" <?php post_class(); ?>><h1><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h1><h3 class="postDate"><?php the_date($date_format); ?> at <?php the_time($time_format); ?></h3><h5 class="postTags"><?php the_tags(); ?></h5><div class="postThumb"><?php the_post_thumbnail(); ?></div><div class="postContent"><?php if($post->post_excerpt) the_excerpt(); else { the_content();} ?></div></div><?php endwhile; if ( function_exists('base_pagination') ) { base_pagination(); } else if ( is_paged() ) {} ?><?php else: ?><p><h1><?php _e('Nothing Found','boiler'); ?></h1></p><?php endif; ?>