<?php $date_format = get_option( 'date_format' );$time_format = get_option( 'time_format' ); get_header(); if ( have_posts() ) while ( have_posts() ) : the_post(); ?><div id="main"><div id="content"><div id="post"><h1><?php the_title(); ?></h1><h3 class="postDate"><?php the_date($date_format); ?> at <?php the_time($time_format); ?></h3><h5 class="postTags"><?php the_tags(); ?></h5><div class="postThumb"><?php the_post_thumbnail(); ?></div><div class="postContent"><?php the_content(); wp_link_pages(); ?></div></div><?php comments_template(); ?><ul id="blogSidebar"><?php if ( !function_exists('dynamic_sidebar') || !dynamic_sidebar("sidebar") ) : endif; ?></ul></div></div><?php endwhile; get_footer(); ?>